import flask
import json
import requests
URL = 'https://discovery-stb3.ertelecom.ru/api/v3/pages/search?text=зик&limit=10'

DEFAULT_HEADERS = {
        'View': 'stb3',
        'X-Auth-Token': ''
}



def findMovie():
    global URL
    global DEFAULT_HEADERS
    found = requests.get(url=URL,headers=DEFAULT_HEADERS)
    return str(found.json())


print(findMovie())